mapper.select:
select `post_id`,`publish_user_id`,`post_title`,`post_content`,`post_category_id`,`post_category_name`,`post_status`,`post_views`,`post_comments`,`post_collects`,`last_update_time`,`create_time`,`media_url`,`is_del` from tb_bbs_post

     and post_id = #{postId} 
     and publish_user_id = #{publishUserId} 
     and post_title = #{postTitle} 
     and post_content = #{postContent} 
     and post_category_id = #{postCategoryId} 
     and post_category_name = #{postCategoryName} 
     and post_status = #{postStatus} 
     and post_views = #{postViews} 
     and post_comments = #{postComments} 
     and post_collects = #{postCollects} 
     and last_update_time = #{lastUpdateTime} 
     and create_time = #{createTime} 
     and media_url = #{mediaUrl} 
     and is_del = #{isDel} 
    and del_flag = 0

order by post_id desc limit 1

select `post_id`,`publish_user_id`,`post_title`,`post_content`,`post_category_id`,`post_category_name`,`post_status`,`post_views`,`post_comments`,`post_collects`,`last_update_time`,`create_time`,`media_url`,`is_del` from tb_bbs_post

    and post_id = #{postId}
    and publish_user_id = #{publishUserId}
    and post_title = #{postTitle}
    and post_content = #{postContent}
    and post_category_id = #{postCategoryId}
    and post_category_name = #{postCategoryName}
    and post_status = #{postStatus}
    and post_views = #{postViews}
    and post_comments = #{postComments}
    and post_collects = #{postCollects}
    and last_update_time = #{lastUpdateTime}
    and create_time = #{createTime}
    and media_url = #{mediaUrl}
    and is_del = #{isDel}
    and del_flag = 0



        order by ${orderBySql}


        order by post_id desc



    ${limitCondition}

mapper.update:
update tb_bbs_post
`publish_user_id` = #{publishUserId}, `post_title` = #{postTitle}, `post_content` = #{postContent}, `post_category_id` = #{postCategoryId}, `post_category_name` = #{postCategoryName}, `post_status` = #{postStatus}, `post_views` = #{postViews}, `post_comments` = #{postComments}, `post_collects` = #{postCollects}, `last_update_time` = #{lastUpdateTime}, `create_time` = #{createTime}, `media_url` = #{mediaUrl}, `is_del` = #{isDel}
where post_id = #{postId} and del_flag = 0
